windowclass.script:
function onInit()
	if User.isHost() then
		registerMenuItem("Delete Item", "delete", 6);
		registerMenuItem("Confirm Delete", "delete", 6, 7);
	else
		isgmonly.setVisible(false);
	end
end

function onMenuSelection(selection, subselection)
	if selection == 6 and subselection == 7 then
		getDatabaseNode().delete();
	end
end

function onDrag(button, x, y, draginfo)
	if label.getValue() ~= "" then
		draginfo.setType("number");
		draginfo.setDescription(label.getValue());
		draginfo.setStringData(label.getValue());
		draginfo.setNumberData(bonus.getValue());
	end

	return true;
end

function onDoubleClick(x, y)
	ModifierStack.addSlot(label.getValue(), bonus.getValue());
end

function onDrop(x, y, draginfo)
	return windowlist.onDrop(x, y, draginfo);
end

function onGainFocus()
	window.setFrame("rowshade");
end

function onLoseFocus()
	window.setFrame(nil);
end

jpgnumberfield.script:
function onDrag(button, x, y, draginfo)
	if dragging then
		return true;
	end

	dragging = window.onDrag(button, x, y, draginfo);
	return dragging;
end

function onDragEnd(draginfo)
	dragging = false;
end

function onDoubleClick(x, y)
	return window.onDoubleClick(x, y);
end

function onDrop(x, y, draginfo)
	if draginfo.getType() ~= "number" then
		return false;
	end
end

textlistitemvalue.script:
function onDrag(button, x, y, draginfo)
	if dragging then
		return true;
	end

	dragging = window.onDrag(button, x, y, draginfo);
	return true;
end

function onDragEnd(draginfo)
	dragging = false;
end

function onDoubleClick(x, y)
	return window.onDoubleClick(x, y);
end

windowlist.script:
function onInit()
	if User.isHost() then
		registerMenuItem("Create Item", "insert", 5);
	end
end

function createItem()
	local wnd = NodeManager.createWindow(self);
	if wnd then
		wnd.label.setFocus();
	end
	return wnd;
end

function onMenuSelection(selection)
	if selection == 5 then
		createItem();
	end
end

function onClickDown(button, x, y)
	return true;
end

function onClickRelease(button, x, y)
	if User.isHost() then
		if not getNextWindow(nil) then
			createItem();
		end
	end
	return true;
end

function onSortCompare(w1, w2)
	return w1.label.getValue() > w2.label.getValue();
end

function onDrop(x, y, draginfo)
	if User.isHost() then
		if draginfo.getType() == "number" then
			local wnd = NodeManager.createWindow(self);
			if wnd then
				wnd.label.setValue(draginfo.getDescription());
				wnd.bonus.setValue(draginfo.getNumberData());
			end
			return true;
		end
	end
end

function onFilter(w)
	if not User.isHost() and w.isgmonly.getState() then
		return false;
	end

	local f = string.lower(window.filter.getValue());
	if f == "" then
		return true;
	end

	if string.find(string.lower(w.label.getValue()), f, 0, true) then
		return true;
	end

	return false;
end
